-- create table for users(patients / individuals) && table for hospitals with services as a SET 

CREATE TABLE IF NOT EXISTS users (
	id INT NOT NULL AUTO_INCREMENT, 
	email VARCHAR(255) NOT NULL UNIQUE, 
	name VARCHAR(255) NOT NULL,
	phone_number VARCHAR(255), 
	PRIMARY KEY(id)
);

CREATE TABLE IF NOT EXISTS hospitals (
	id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
	email VARCHAR(255) NOT NULL UNIQUE,
	name VARCHAR(255) NOT NULL,
	country VARCHAR(255) NOT NULL,
	region VARCHAR(255) NOT NULL,
	operating_hours VARCHAR(255),
	phone_number VARCHAR(255),
	services SET('Accident and emergency (A&E)', 'Burns Unit', 'Cardiology', 'Coronary Care Unit (CCU)', 'Critical Care/Intensive Care Unit (ICU)', 'Dentistry', 'Endoscopy', 'Elderly Services', 'Gastroenterology', 'General Surgery', 'Gynecology & Obstetrics', 'Hematology', 'Internal Medicine', 'Maternity', 'Microbiology', 'Neonatal', 'Nephrology', 'Neurology', 'Nutrition and Dietetics', 'Occupational Therapy (OT)', 'Oncology', 'Ophthalmology', 'Orthopedics', 'Otolaryngology (Ear_Nose_Throat)', 'Paediatrics', 'Pharmacy', 'Physiotherapy', 'Psychiatry', 'Radiology', 'Radiotherapy', 'Renal', 'Rheumatology', 'Urology') NOT NULL,
	website VARCHAR(255)
);

